import { useState } from "react";

const ChatSidebar = () =>
{
    const [searchChannel, setSearchChannel] = useState(false);
    const [addChannel, setAddChannel] = useState(false);

    return (
        <div className="chat-sidebar">
            <div className="sidebar-header">
                <p>CHANNELS</p>
                <div>
                    <button onClick={() => setSearchChannel(!searchChannel)}>
                        <i className="fas fa-search"></i>
                    </button>
                    <button onClick={() => setAddChannel(!addChannel)}>
                        <i className="fas fa-plus"></i>
                    </button>
                </div>
            </div>
            {searchChannel && 
                <div>
                    <input type="text" placeholder="Search Channel" />
                </div>
            }
            {addChannel && 
                <div>
                    <input type="text" placeholder="Add Channel" />
                </div>
            }
            {/* Rest of the sidebar content here */}
        </div>
    );
};

export default ChatSidebar;
