import { useState } from "react";

const ChatSidebar = () => {
    const [searchChannel, setSearchChannel] = useState(false);
    const [addChannel, setAddChannel] = useState(false);

    return (
        <div className="chat-sidebar">
            <div className="channels-header">
                <h2>CHANNELS</h2>
                <button onClick={() => setSearchChannel(!searchChannel)}><i className="fa fa-search"></i></button>
                <button onClick={() => setAddChannel(!addChannel)}><i className="fa fa-plus"></i></button>
            </div>
            {searchChannel && <input type="text" placeholder="Search channel..." />}
            {addChannel && <input type="text" placeholder="Add channel..." />}
            {/* Reste du contenu de la barre lat√©rale ici */}
        </div>
    );
};

export default ChatSidebar;
