import { useEffect, useState } from 'react';
import { ax } from '../../services/axios/axios'
import { UserInfos } from '../../services/interfaces/userInfos.interface';
import axios from 'axios';
import { useForm } from 'react-hook-form';
// import QRCode from 'react-qr-code';

const TfaPage = () => {

	const [userInfos, setUserInfos] = useState<UserInfos | null>(null); //creer une interface afin d'acceder a tout
	const [qrCodeUrl, setQRCodeUrl] = useState('');
	const token = localStorage.getItem("token");
	const { register, handleSubmit, reset } = useForm();

	useEffect(() => {
		const getUsers = async () => {
			try {
				const response = await axios.get("http://localhost:8080/users/me", {
					headers: {
						Authorization: `Bearer ${token}`,
					},
				});
				setUserInfos(response.data);
			} catch (error) {
				console.error("Failed to fetch users.");
			}
		};
		getUsers();
	}, [token]);

	const getCode = async () => {
		try {
			const response = await ax.post("http://localhost:8080/users/qrcode",
				{ name: userInfos?.name }, {
				headers: {
					Authorization: `Bearer ${token}`,
				},
			}
			);
			setQRCodeUrl(response.data);
		}
		catch (e) {
			console.log("could not get qrcode");
		}
	}
	// getCode();

	// const QRCodeComponent = ({ qrCodeUrl }: any) => {
	// 	return <QRCode value={qrCodeUrl} />;
	//   };

	const handleChanges = () => {

	}

	return (
		<div>
			<button onClick={getCode}>Get qr code</button>
			{/* {qrCodeUrl && <QRCodeComponent qrCodeUrl={qrCodeUrl} />} */}
			{/* <QRCode
				value={qrCodeUrl}
			/> */}
			<img src={qrCodeUrl} alt=""/>
			<form onSubmit={handleSubmit(handleChanges)}>
				<label>Enter your verification code:</label>
				<input
					type="text"
					{...register('code')}
				/>
				<button type='submit' value='s'>Submit</button>
			</form>
		</div>
	);
};

export default TfaPage;
