import React, { useState } from 'react';
import './PrivateMessageForm.css';
import { io } from "socket.io-client";

const socket = io("http://localhost:8080"); // Assurez-vous d'avoir la bonne URL de votre serveur ici.


const PrivateMessageForm = () =>
{
	const [messages, setMessages] = useState([]);

	useEffect(() =>
	{
		socket.on('dmToClient', (message) =>
		{
		setMessages(oldMessages => [...oldMessages, message]);
		});

	return () =>
	{
		socket.off('dmToClient');
	};
	}, []);

	const handleSubmit = (e: React.FormEvent) =>
	{
		e.preventDefault();

		// logic to send dm
		socket.emit('dmToServer',
		{
			senderId: user.id, // à remplacer par l'id de l'utilisateur connecté
			receiverId: receiverId, // à remplacer par l'id de l'utilisateur à qui vous voulez envoyer le message
			content: message,
		});
		setMessage('');
	};

	return (
		<form className="private-message-form-wrapper" onSubmit={handleSubmit}>
		  <input 
			type="text"
			placeholder="Message"
			maxLength={4096}
			className="message-input"
			value={message}
			onChange={e => setMessage(e.target.value)}
		  />
		</form>
	  );
};

export default PrivateMessageForm;
