import { Link } from "react-router-dom";
import Content from "../components/content"
import Header from "../components/header"
import { useState } from "react";
import { ax } from '../axios/axios'
import { AxiosError } from 'axios'

const ProfilePage = () => {

	const [nameInput, setName] = useState('');

	const handleChanges = async () => {
		if (nameInput === undefined)
			return ;
		try {
			await ax.patch('users', {
				name: nameInput,
			});
		}
		catch (error) {
			const axiosError = error as AxiosError<{ message: string; statusCode: number }>;
			if (axiosError?.response?.data?.statusCode === 200) {
				const message = document.getElementById("message");
				if (message)
					message.textContent = "This userName is already taken";
				console.log("This userName is already taken");
			}
		}
	};

	return (
		<div>
			<Header name=""/>
			<Content>
				<h1>User profile</h1>
				<br></br>
					<h2>Update any available field as you please</h2>
				<br></br>
				<br></br>
					<input
						type="text"
						value={nameInput}
						onChange={(event) => setName(event.target.value)}
					/>
				<br></br>
				<br></br>
					<button onClick={handleChanges}>Submit</button>
					<div id="message"></div>
				<br></br>
				<br></br>
				<Link to="/home">Home</Link>
				<br></br>
			</Content>
		</div>
	);

};

export default ProfilePage;
